plot_lifeexp_gdp <- function(df, year=2007, continent='All'){ dsub <- dplyr::filter(df, year==year); if(continent!='All'){ dsub <- dplyr::filter(dsub, continent==continent) }; ggplot2::ggplot(dsub, ggplot2::aes(x=gdpPercap, y=lifeExp, size=pop, color=continent)) + ggplot2::geom_point(alpha=.7) + ggplot2::scale_x_log10(labels=scales::label_number_si()) + ggplot2::labs(x='GDP per capita (log scale)', y='Life expectancy', size='Population', color='Continent') + ggplot2::theme_minimal() }
